const pikachu=document.getElementById("pikachu"),pikachuCry=document.getElementById("pikachu-cry");document.getElementById("pikachu-container").addEventListener("click",()=>{pikachu.classList.add("pikachu-smile"),pikachuCry.play(),setTimeout(()=>pikachu.classList.remove("pikachu-smile"),1300)}),document.getElementById("skin").addEventListener("change",function(e){console.log("Skin color changed to:",e.target.value),document.documentElement.style.setProperty("--skin-color",e.target.value)}),document.getElementById("earsEyesNose").addEventListener("change",function(e){document.documentElement.style.setProperty("--ear-eye-nose-color",e.target.value)}),document.getElementById("eyeGlint").addEventListener("change",function(e){document.documentElement.style.setProperty("--eye-glint-color",e.target.value)}),document.getElementById("cheeks").addEventListener("change",function(e){document.documentElement.style.setProperty("--cheek-color",e.target.value)}),document.getElementById("mouth").addEventListener("change",function(e){document.documentElement.style.setProperty("--mouth-color",e.target.value)}),document.getElementById("tongue").addEventListener("change",function(e){document.documentElement.style.setProperty("--tongue-color",e.target.value)}),document.getElementById("resetButton").addEventListener("click",function(){document.documentElement.style.setProperty("--skin-color","#ffd823"),document.documentElement.style.setProperty("--ear-eye-nose-color","#000"),document.documentElement.style.setProperty("--eye-glint-color","#ffffff"),document.documentElement.style.setProperty("--cheek-color","#fe2f1d"),document.documentElement.style.setProperty("--mouth-color","#74070a"),document.documentElement.style.setProperty("--tongue-color","#f45490")});